mapper.select:
SELECT * FROM ( SELECT ROWNUM R, B.* FROM ( SELECT P.*, M.USERID, PI.FILENO, PI.ORIGINALNAME, PI.FILENAME FROM PRODUCT P INNER JOIN MEMBER M ON P.USERNO = M.USERNO LEFT JOIN PRODUCTIMAGE PI ON P.PRONO = PI.PRONO WHERE P.PRONO > 0 AND P.CATNO = #{catNo}
AND P.PRONAME LIKE '%'||#{search}||'%'
AND P.PROCONTENTS LIKE '%'||#{search}||'%'
AND M.USERID LIKE '%'||#{search}||'%'
ORDER BY P.CREATEDATE DESC ) B )
WHERE R BETWEEN #{startRow} AND #{lastRow}

SELECT FILENO, ORIGINALNAME, FILENAME
FROM PRODUCTIMAGE
WHERE PRONO = #{proNo}
